import React, { useState } from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
import { useSpring, animated, config } from "react-spring";
import delay from "delay";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx, Grid, Box, Image, Button, Text, Styled } from "theme-ui";

const interp = (r) =>
  `translate3d(0, ${15 * Math.sin(r + (2 * Math.PI) / 1.6)}px, 0)`;
function MainPage() {
  const { radians } = useSpring({
    to: async (next) => {
      while (1) await next({ radians: 2 * Math.PI });
    },
    from: { radians: 0 },
    config: { duration: 5000 },
    reset: true,
  });
  return (
    <div
      sx={{
        fonts: '"Avenir Next", sans-serif',

        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100vh",
        width: "100vw",
        bg: "#6930c3",
      }}
    >
      <header sx={{ width: "100%" }}>
        <Button mr={2}>About Character</Button>
      </header>
      <main sx={{ height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "4fr 3fr"]}>
          <Box
            sx={{
              width: ["100%", "80%"],
              height: ["100%", "80%"],
              color: "#ffffff",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Styled.h1 sx={{ fontSize: [6, 7, 8], lineHeight: 1.5 }}>
              Meet Paimon[title]
            </Styled.h1>
            <Styled.h2 sx={{ fontSize: [5, 6, 7] }}>Sub Title</Styled.h2>
            <Styled.div>
              {" "}
              <Styled.h4 sx={{ lineHeight: 1.5 }}>Description</Styled.h4>
            </Styled.div>
            <Styled.div>
              <Styled.h6>Want to download? Join:</Styled.h6>
            </Styled.div>
          </Box>
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <animated.h1
              style={{
                transform: radians.interpolate(interp),
              }}
            >
              <Image src="/img/paimon.png" width="450" height="450" />
            </animated.h1>
          </Box>
        </Grid>
      </main>
      <footer>hi</footer>
    </div>
  );
}

export default MainPage;
