import React, { useState } from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
import { useSpring, animated } from "react-spring";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx } from "theme-ui";
import { Grid, Box, Image } from "theme-ui";

function MainPage() {
  // You can create keyframes for springs and trails
  const Container = Keyframes.Spring({
    // Single props
    show: { opacity: 1 },
    // Chained animations (arrays)
    showAndHide: [{ opacity: 1 }, { opacity: 0 }],
    // Functions with side-effects with access to component props
    wiggle: async (next, cancel, ownProps) => {
      await next({ x: 100 });
      await delay(1000);
      await next({ x: 0 });
    },
  });
  return (
    <div
      sx={{
        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100vh",
        width: "100vw",
        bg: "#E7B996",
      }}
    >
      <header sx={{ width: "100%", border: "2px solid black" }}>Header</header>
      <main sx={{ border: "2px solid black", height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "4fr 3fr"]}>
          <Box sx={{ border: "2px solid black" }}></Box>
          <Box
            sx={{
              border: "2px solid black",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Container state="showAndHide">
              {(styles) => <div>Hello</div>}
            </Container>
            <Image src="/img/paimon.png" width="450" height="450" />
          </Box>
        </Grid>
      </main>
      <footer>hi</footer>
    </div>
  );
}

export default MainPage;
