import React from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx } from "theme-ui";
import { Grid, Box, Image } from "theme-ui";

const Content = Keyframes.Spring(async (next) => {
  // None of this will cause React to render, the component renders only once :-)
  while (true) {
    await next({
      from: { opacity: 0, width: 50, height: 50, background: "black" },
      opacity: 1,
      width: 80,
      height: 80,
      background: "tomato",
    });
    await next({
      from: { left: "0%" },
      left: "70%",
      background: "seagreen",
    });
    next({
      from: { top: "0%" },
      top: "40%",
      background: "plum",
      config: config.wobbly,
    });
    await delay(2000); // don't wait for the animation above to finish, go to the next one in 2s
    await next({ left: "0%", background: "hotpink" });
    await next({
      top: "0%",
      background: "teal",
    });
    await next({
      opacity: 0,
      width: 40,
      height: 40,
      background: "black",
    });
  }
});
function MainPage() {
  return (
    <div
      sx={{
        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100vh",
        width: "100vw",
        bg: "#E7B996",
      }}
    >
      <header sx={{ width: "100%", border: "2px solid black" }}>Header</header>
      <main sx={{ border: "2px solid black", height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "4fr 3fr"]}>
          <Box sx={{ border: "2px solid black" }}></Box>
          <Box
            sx={{
              border: "2px solid black",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Image src="/img/paimon.png" width="450" height="450" />
          </Box>
        </Grid>
      </main>
      <footer>hi</footer>
    </div>
  );
}

export default MainPage;
