import React from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
import { useSpring, animated } from "react-spring";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx } from "theme-ui";
import { Grid, Box, Image } from "theme-ui";

function MainPage() {
  const { x, c } = useSpring({
    from: { xy: [0, 0], c: 0 },
    x: on ? 1 : 0,
    c: on ? 1 : 0,
  });
  return (
    <div
      sx={{
        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100vh",
        width: "100vw",
        bg: "#E7B996",
      }}
    >
      <header sx={{ width: "100%", border: "2px solid black" }}>Header</header>
      <main sx={{ border: "2px solid black", height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "4fr 3fr"]}>
          <Box sx={{ border: "2px solid black" }}></Box>
          <Box
            sx={{
              border: "2px solid black",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <animated.h1
              style={{
                transform: x
                  .interpolate({
                    range: [0, 0.25, 0.5, 0.75, 1],
                    output: [0, 500, 200, 800, 500],
                  })
                  .interpolate((x) => `translateX(${x}px)`),

                color: c
                  .interpolate({
                    range: [0, 0.5, 1],
                    output: ["red", "blue", "green"],
                  })
                  .interpolate((c) => c),
              }}
            >
              >Hello
            </animated.h1>
            <Image src="/img/paimon.png" width="450" height="450" />
          </Box>
        </Grid>
      </main>
      <footer>hi</footer>
    </div>
  );
}

export default MainPage;
