import React, { useState } from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
import { useSpring, animated, config } from "react-spring";
import delay from "delay";
import Button from "./button.js";
import { Colors } from "./colors";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx, Grid, Box, Image, Text, Styled, AspectImage } from "theme-ui";

const interp = (r) =>
  `translate3d(0, ${15 * Math.sin(r + (2 * Math.PI) / 1.6)}px, 0)`;
function MainPage(props) {
  const { radians } = useSpring({
    to: async (next) => {
      while (1) await next({ radians: 2 * Math.PI });
    },
    from: { radians: 0 },
    config: { duration: 5000 },
    reset: true,
  });
  return (
    <div
      sx={{
        fonts: '"Avenir Next", sans-serif',
        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100%",
        width: "100%",
        bg: `${props.backgroundColor}`,
      }}
    >
      <header sx={{ display: "flex", alignItems: "center" }}>
        <div sx={{ mx: "auto" }} />
        <Button
          data="About Character"
          textColorBtn={props.textColorBtn}
          backgroundColorBtn={props.backgroundColorBtn}
        />
      </header>
      <main sx={{ height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "1fr 1fr"]}>
          <Box
            sx={{
              width: "100%",

              color: `${props.primaryText}`,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Styled.div
              sx={{
                width: ["50%", "50%", "60%"],
              }}
            >
              <Styled.h1
                sx={{
                  fontSize: [5, 6, 7],
                  lineHeight: 1.5,
                  fontWeight: "bold",
                }}
              >
                {props.title}
              </Styled.h1>
              <Styled.h2 sx={{ fontSize: [4, 5, 6], lineHeight: 1.5 }}>
                {props.sub_title}
              </Styled.h2>
              <Styled.div>
                {" "}
                <Styled.h6 sx={{ fontSize: [3, 4], lineHeight: 1.5 }}>
                  {props.description}
                </Styled.h6>
              </Styled.div>
              <Styled.div sx={{ mt: 10 }}>
                <Styled.h6>Want to download? Join:</Styled.h6>
              </Styled.div>
            </Styled.div>
          </Box>
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <animated.div
              style={{
                transform: radians.interpolate(interp),
              }}
            >
              <Image
                src={props.png}
                sx={{
                  width: ["50%", "80%"],
                }}
              />
            </animated.div>
          </Box>
        </Grid>
      </main>
      <footer sx={{}}>Powered by</footer>
    </div>
  );
}

export default MainPage;
