import React from "react";
import ReactDOM from "react-dom";
import { Keyframes } from "react-spring/renderprops";
/** @jsxRuntime classic */
/** @jsx jsx */
import { jsx } from "theme-ui";
import { Grid, Box, Image } from "theme-ui";

const AnimateCharacter = Keyframes.Spring(async (next) => {
  while (true) {
    await next({
      from: {
        top: "0%",
      },
      top: "10%",
    });
  }
});
function MainPage() {
  return (
    <div
      sx={{
        display: "flex",
        flexDirection: "column",
        // minHeight: '100vh',
        height: "100vh",
        width: "100vw",
        bg: "#E7B996",
      }}
    >
      <header sx={{ width: "100%", border: "2px solid black" }}>Header</header>
      <main sx={{ border: "2px solid black", height: "100%" }}>
        <Grid sx={{ height: "100%", width: "100%" }} columns={[1, "4fr 3fr"]}>
          <Box sx={{ border: "2px solid black" }}></Box>
          <Box
            sx={{
              border: "2px solid black",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <AnimateCharacter native>
              <Image src="/img/paimon.png" width="450" height="450" />
            </AnimateCharacter>
          </Box>
        </Grid>
      </main>
      <footer>hi</footer>
    </div>
  );
}

export default MainPage;
